<script>


import PauseMockModal from './PauseMockModal.vue';
import { mapGetters } from "vuex";
import { get } from "../views/lib/api";
import CircularProgress from './CircularProgress.vue';


export default {
  components: {
    PauseMockModal,
    CircularProgress
  },

  props: {
    flagcounts: {

    },
    remainingTimeInSeconds: {

    },
    formattedTime: {

    },
    stoptimerpopup: {},
    fillicon: {},
    isRowFixed:{},
    correctcount:{},
    incorrectcount:{},
    unanswered:{},
    roundedPercent:{}




  },
  data() {
    return {
      isMainDropdownOpen: false,
      isNestedDropdownOpen: false,
      showPauseModal: false,
      scrollPosition: 0,
      showclock: false,
      filleye: false,
      mockIndex: 0,
      coloring: '#fff',
      bubbles: null,


    }
  },

  created() {

    this.coloring = this.remainingTimeInSeconds <= 600 ? 'yellow' : 'white'

    this.mockIndex = parseInt(localStorage.getItem('mockindex'), 10);


    get("/getglobalhelp").then((res) => {
        console.log('data', res.data.data)

        this.bubbles = res.data.data.value == 0 ? false: true

      });

  },

  computed: {
    ...mapGetters(["user"]), // your getter is named "user"
  },
  methods: {


    globalhelp() {

      console.log('eee', this.bubbles)

      get("/globalhelp?value=" + this.bubbles).then((res) => {
        console.log('data', res.data.data)
        this.$emit('globalhelp')

        this.bubbles = res.data.data.value == 0 ? false: true

      });

    },

    logout() {
      localStorage.removeItem("token");
      this.$store.dispatch("user", null);
      this.$router.push("/");
    },

    PauseModal() {
      this.showPauseModal = true
      this.$emit('showstoptimer')

    },

    Resuming() {
      this.showPauseModal = false
      this.$emit('startagain')
    },

    notifyParent() {
      console.log('hello')
      this.filleye = true

      this.$emit('birdseye');
    },


    notifyParentQuestion() {
      console.log('hellossss')
      this.filleye = false

      this.$emit('returnquestion');
    },




    toggleMainDropdown() {
      this.isMainDropdownOpen = !this.isMainDropdownOpen;
      if (this.isMainDropdownOpen) {
        document.addEventListener('click', this.handleOutsideClick);
      }
    },
    toggleNestedDropdown() {
      this.isNestedDropdownOpen = !this.isNestedDropdownOpen;
    },
    handleOutsideClick(event) {
      // if (!this.$refs.dropdown.contains(event.target)) {
      //   this.isMainDropdownOpen = false;
      //   document.removeEventListener('click', this.handleOutsideClick);
      // }

      const dropdown = this.$refs.dropdown;
      if (dropdown && !dropdown.contains(event.target)) {
        this.isMainDropdownOpen = false;
        document.removeEventListener('click', this.handleOutsideClick);
      }
    },
    handleExitMock() {
      this.showPauseModal = false;
      this.$emit('exitmock')

      // this.$router.push('/mocksection'); 
    },
    lockBodyScroll() {
      this.scrollPosition = window.pageYOffset;
      document.body.classList.add('body-scroll-lock');
      document.body.style.top = `-${this.scrollPosition}px`;
    },

    unlockBodyScroll() {
      document.body.classList.remove('body-scroll-lock');
      window.scrollTo(0, this.scrollPosition);
      document.body.style.top = '';
    },
  },
  watch: {
    showPauseModal(newVal) {
      if (newVal) {
        this.lockBodyScroll();
      } else {
        this.unlockBodyScroll();
      }
    }
  },
  beforeUnmount() {
    if (this.showPauseModal) {
      this.unlockBodyScroll();
    }
  }
}

</script>



